import React, { useState, useRef } from 'react';
import './App.css';

const tracks = [
    { src: '/public/music/lofi1.mp3', name: 'Track 1' },
    { src: '/public/music/lofi2.mp3', name: 'Track 2' },
    { src: '/public//music/lofi3.mp3', name: 'Track 3' },
    { src: '/public//music/lofi4.mp3', name: 'Track 4' },
    { src: '/public//music/lofi5.mp3', name: 'Track 5' },
    { src: '/public//music/lofi6.mp3', name: 'Track 6' },
    { src: '/public//music/lofi7.mp3', name: 'Track 7' },
    { src: '/public//music/lofi8.mp3', name: 'Track 8' },
    { src: '/public//music/lofi9.mp3', name: 'Track 9' },
];

function App() {
    const [currentTrackIndex, setCurrentTrackIndex] = useState(0);
    const [showPlanner, setShowPlanner] = useState(false);
    const [showNoteArea, setShowNoteArea] = useState(false);
    const [showDropArea, setShowDropArea] = useState(false);
    const [tasks, setTasks] = useState([]);
    const [background, setBackground] = useState('/images/background1.gif');
    const [notes, setNotes] = useState('');
    const taskInputRef = useRef();
    const audioPlayerRef = useRef();

    const loadTrack = (index) => {
        audioPlayerRef.current.src = tracks[index].src;
        audioPlayerRef.current.load();
        audioPlayerRef.current.play();
    };

    const nextTrack = () => {
        setCurrentTrackIndex((currentTrackIndex + 1) % tracks.length);
    };

    const previousTrack = () => {
        setCurrentTrackIndex((currentTrackIndex - 1 + tracks.length) % tracks.length);
    };

    const togglePlanner = () => {
      setShowPlanner(!showPlanner);
      console.log('Planner visibility:', !showPlanner);
  };




    const toggleDropArea = () => setShowDropArea(!showDropArea);

    const toggleNoteArea = () => setShowNoteArea(!showNoteArea);

    const changeBackground = (event) => setBackground(`/images/${event.target.value}`);

    const addTask = () => {
        const taskText = taskInputRef.current.value.trim();
        if (taskText !== '') {
            setTasks([...tasks, { text: taskText, completed: false }]);
            taskInputRef.current.value = '';
        }
    };

    const toggleTaskCompletion = (index) => {
        const updatedTasks = [...tasks];
        updatedTasks[index].completed = !updatedTasks[index].completed;
        setTasks(updatedTasks);
    };

    const removeTask = (index) => {
        const updatedTasks = tasks.filter((_, i) => i !== index);
        setTasks(updatedTasks);
    };

    const exportNotes = () => {
        const blob = new Blob([notes], { type: 'text/plain' });
        const url = URL.createObjectURL(blob);
        const a = document.createElement('a');
        a.href = url;
        a.download = 'notes.txt';
        a.click();
        URL.revokeObjectURL(url);
    };

    const toggleFullscreen = () => {
      if (!document.fullscreenElement) {
          if (document.documentElement.requestFullscreen) {
              document.documentElement.requestFullscreen();
          } else if (document.documentElement.mozRequestFullScreen) { // Firefox
              document.documentElement.mozRequestFullScreen();
          } else if (document.documentElement.webkitRequestFullscreen) { // Chrome, Safari and Opera
              document.documentElement.webkitRequestFullscreen();
          } else if (document.documentElement.msRequestFullscreen) { // IE/Edge
              document.documentElement.msRequestFullscreen();
          }
      } else {
          if (document.exitFullscreen) {
              document.exitFullscreen();
          } else if (document.mozCancelFullScreen) { // Firefox
              document.mozCancelFullScreen();
          } else if (document.webkitExitFullscreen) { // Chrome, Safari and Opera
              document.webkitExitFullscreen();
          } else if (document.msExitFullscreen) { // IE/Edge
              document.msExitFullscreen();
          }
      }
  };


    const handleFileDrop = (event) => {
        event.preventDefault();
        const files = event.dataTransfer.files;
        if (files.length > 0) {
            handleFile(files[0]);
        }
    };

    const handleFile = (file) => {
        const fileType = file.type;
        const fileURL = URL.createObjectURL(file);

        if (fileType === 'application/pdf') {
            document.getElementById('file-preview').src = fileURL;
        } else if (fileType.startsWith('image/')) {
            const img = document.createElement('img');
            img.src = fileURL;
            img.style.width = '100%';
            img.style.height = 'auto';
            document.getElementById('drop-area').appendChild(img);
        } else if (fileType.startsWith('video/')) {
            const videoElement = document.createElement('video');
            videoElement.src = fileURL;
            videoElement.controls = true;
            document.getElementById('drop-area').appendChild(videoElement);
        } else if (fileType === 'application/vnd.ms-powerpoint' || fileType === 'application/vnd.openxmlformats-officedocument.presentationml.presentation') {
            const viewerURL = `https://docs.google.com/gview?url=${fileURL}&embedded=true`;
            document.getElementById('drop-area').innerHTML = `<iframe src="${viewerURL}" width="100%" height="100%"></iframe>`;
        } else {
            document.getElementById('drop-area').innerHTML = `File type not supported for preview: <strong>${file.name}</strong>`;
        }
    };

    return (
        <div style={{ backgroundImage: `url(${background})` }} className="App">
            <div id="topRightText"><img src="logo.png" alt="Logo" height="100" width="300" /></div>

            <div id="musicPlayer">
                <button onClick={previousTrack} title="Previous Track">&#9664;</button>
                <audio ref={audioPlayerRef} controls>
                    <source src={tracks[currentTrackIndex].src} type="audio/mp3" />
                    Your browser does not support the audio element.
                </audio>
                <button onClick={nextTrack} title="Next Track">&#9654;</button>
                <button onClick={togglePlanner} title="Planner">📅</button>
                <button onClick={toggleDropArea} title="File viewer">📂</button>
                <button onClick={toggleNoteArea} title="Notes">📝</button>
                <button onClick={toggleFullscreen} title="Fullscreen">⛶</button>

                <div id="backgroundSelector">
                    <select onChange={changeBackground} value={background}>
                        <option value="background1.gif">Blue Lagoon</option>
                        <option value="background4.gif">Cafe</option>
                        <option value="background6.gif">Beachside Living room</option>
                        <option value="background7.gif">Sunset Window</option>
                        <option value="background8.gif">Midnight Street</option>
                    </select>
                </div>
            </div>

            {showNoteArea && (
                <div id="noteArea">
                    <textarea
                        id="noteTextArea"
                        value={notes}
                        onChange={(e) => setNotes(e.target.value)}
                        placeholder="Write your notes here..."
                    ></textarea>
                    <button onClick={exportNotes}>Save Notes</button>
                </div>
            )}

            {showPlanner && (
                <div id="planner">
                    <h2>My Planner</h2>
                    <input
                        type="text"
                        ref={taskInputRef}
                        placeholder="Enter a new task..."
                    />
                    <button onClick={addTask}>Add Task</button>
                    <div id="tasks">
                        {tasks.map((task, index) => (
                            <div key={index} className={`task ${task.completed ? 'completed' : ''}`}>
                                <input
                                    type="checkbox"
                                    checked={task.completed}
                                    onChange={() => toggleTaskCompletion(index)}
                                />
                                {task.text}
                                <button onClick={() => removeTask(index)}>Remove</button>
                            </div>
                        ))}
                    </div>
                </div>
            )}

            {showDropArea && (
                <div
                    id="drop-area"
                    onDragOver={(e) => e.preventDefault()}
                    onDrop={handleFileDrop}
                >
                    Drag & Drop any file here
                    <embed id="file-preview" style={{ display: 'none', width: '100%', height: '100%', borderRadius: '10px' }} />
                </div>
            )}
        </div>
    );
}

export default App;
